import axios from "axios";
var SERVER_URL = 'https://candidate.neversitup.com/todo'

const api = axios.create({
  baseURL: SERVER_URL
});

api.interceptors.request.use(async (config) => {
  // const token = localStorage.token;
  const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZmIyN2MxOTgyNWFhNTI5MmIxMDgwZjUiLCJpYXQiOjE2MDU3MDMwNzcsImV4cCI6MTYwNTcxMzg3N30.E8R62nxovgqrb-JE3tgg_Cgg2SuVwBSh3s6RTzn0gjE'
  if (token) {
    config.headers.common = {
      Authorization: `Bearer ${token}`,
    };
  }
  return config;
}, function (error) {
  return Promise.reject(error);
});



const client = axios.create({
  url: SERVER_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

export { client, api };
