import React, { useState, useEffect } from 'react'
import moment from 'moment'
import styled from 'styled-components';
import { Modal } from '../../shared/components/Modal';
import { GlobalStyle } from '../../globalStyles';

var CREATE = 'Create'
var EDIT = 'Edit'

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Button = styled.button`
  min-width: 100px;
  padding: 16px 32px;
  border-radius: 4px;
  border: none;
  background: #141414;
  color: #fff;
  font-size: 24px;
  cursor: pointer;
`;

function Todos() {

    const [inputTitle, setInputTitle] = useState('')
    const [inputDes, setInputDes] = useState('')
    const [data, setdata] = useState([])
    const [isModalShow, setIsModalShow] = useState(false)
    const [isAction, setIsAction] = useState(CREATE)
    // const modalRef = useRef(null);
    const [showModal, setShowModal] = useState(false);

    const openModal = () => {
      setShowModal(prev => !prev);
    };

    useEffect(() => {
        getData()
        return () => {

        }
    }, [])


    const getData = () => {
        let todoList = [
            {
                title: "My todo",
                description: "I'm gonna create some todo list",

            },

        ]

        setdata(todoList)
    }

    const handleSubmit = (e) => {
        console.log('submit : ', e)
        // e.preventDefault()
    }


    const modal = () => {

        // $(modalRef.current).modal('show');
        setIsAction(CREATE)
        setIsModalShow(true)
    }

    const onTodoEdit = () => {
        setIsAction(EDIT)
        setIsModalShow(true)
    }

    return (
        <>
            <section className="row justify-content-center pt-5 pl-3 pr-3">

                {
                    data.length > 0 ?
                        data.map((item, i) => {

                            return (

                                <div onClick={() => openModal} key={i} className='card card-body shadow-sm' style={{ margin: 15 }}>
                                    <div className="modal-header">
                                        <h1>{item.title}</h1>
                                        <button type="button" className="close" aria-label="Close">
                                            <span aria-hidden="true">Ã—</span>
                                        </button>
                                    </div>
                                    <div className="modal-body">
                                        <span>{item.description}</span>
                                    </div>

                                    <div className='modal-footer'>
                                        {moment(new Date()).format('DD-MM-YYYY')}
                                    </div>
                                </div>
                            )
                        })

                        :
                        <div className='card card-body shadow-sm text-center' style={{ margin: 15 }}>
                            <h2 className='text-black-50'>Empty press 'Create'</h2>
                            <h2 className='text-black-50'>for add new todo</h2>
                        </div>

                }



            </section>
            <section className="row justify-content-center pt-5 pl-3 pr-3">
                <button className='btn bg-primary text-white' onClick={openModal} type='button'>+ Create</button>
            </section>

            <Modal title={inputTitle} description={inputDes} showModal={showModal} setShowModal={setShowModal} action={isAction} />
            <GlobalStyle />

        </>
    )

}


export default Todos
