import React, { useState, useEffect, useContext } from 'react';
import TodoForm from './TodoForm';
import Todo from './Todo';
import { getTodoList, createTodo, editTodo, deleteTodo } from '../todo/todo-api-service'
import { useHistory } from 'react-router-dom';
import Axios from 'axios';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const history = useHistory();



  useEffect(() => {
    // console.log(localStorage.getItem('token'))
    if (!localStorage.getItem('token')) {
      history.push(`/`);
      return;
    }

    todoList()
    return () => {

    }
  }, [])

  const todoList = async () => {

    let test = await getTodoList()
    setTodos(test.data)
  }

  const addTodo = async todo => {

    let data = await createTodo(todo)

    if (data) {
      const newTodos = [todo, ...todos];

      setTodos(newTodos);
      console.log(...todos);
    }

  };

  const updateTodo = async (todoId, newValue) => {

    let data = await editTodo(todoId, newValue)
    if (data) {
      setTodos(prev => prev.map(item => (item.id === todoId ? newValue : item)));
    }

  };

  const removeTodo = async id => {
    const SERVER_URL = 'https://candidate.neversitup.com/todo'

    console.log(Axios.get(SERVER_URL + '/todos' ,{
      method: 'get',
      url: SERVER_URL + '/todos',
      headers : {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      }
    }))
    // let data = await deleteTodo(id)

    const removedArr = [...todos].filter(todo => todo._id !== id);

    setTodos(removedArr);
  };

  const completeTodo = id => {
    let updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        todo.isComplete = !todo.isComplete;
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  return (
    <>
      <section className="row justify-content-center pt-5 pl-3 pr-3">
        <h1>Todo List?</h1>
      </section>

      <TodoForm onSubmit={addTodo} />
      <Todo
        todos={todos}
        completeTodo={completeTodo}
        removeTodo={removeTodo}
        updateTodo={updateTodo}
      />
    </>
  );
}

export default TodoList;
