import React, { useState, useEffect, useContext } from 'react';
import TodoForm from './TodoForm';
import Todo from './Todo';
import { getTodoList , createTodo} from '../todo/todo-api-service'
import axios from 'axios';

const token = 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZmIyN2MxOTgyNWFhNTI5MmIxMDgwZjUiLCJpYXQiOjE2MDU2ODc2MzAsImV4cCI6MTYwNTY5ODQzMH0.Y4H8lqQ3Drfaz4ZVpL9LPIC33k_G3YShiOMYwXXO0xM'

function TodoList() {
  const [todos, setTodos] = useState([]);


  useEffect(() => {
    todoList()
    return () => {

    }
  }, [])

  const addTodo = async todo => {
    if (!todo.text || /^\s*$/.test(todo.text)) {
      return;
    }

    await createTodo(todo)
    const newTodos = [todo, ...todos];

    setTodos(newTodos);
    console.log(...todos);
  };

  const todoList = async () => {

    let test = await getTodoList()
    setTodos(test.data)
  }

  const updateTodo = (todoId, newValue) => {
    if (!newValue.text || /^\s*$/.test(newValue.text)) {
      return;
    }

    setTodos(prev => prev.map(item => (item.id === todoId ? newValue : item)));
  };

  const removeTodo = id => {
    const removedArr = [...todos].filter(todo => todo.id !== id);

    setTodos(removedArr);
  };

  const completeTodo = id => {
    let updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        todo.isComplete = !todo.isComplete;
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  return (
    <>
      <section className="row justify-content-center pt-5 pl-3 pr-3">
        <h1>Todo List?</h1>
      </section>

      <TodoForm onSubmit={addTodo} />
      <Todo
        todos={todos}
        completeTodo={completeTodo}
        removeTodo={removeTodo}
        updateTodo={updateTodo}
      />
    </>
  );
}

export default TodoList;
